import { Component, OnInit } from '@angular/core';
import { CalculadoraService } from '../services/calculadora.service';
@Component({
  selector: 'app-calculadora',
  templateUrl: './calculadora.component.html',
  styleUrls: ['./calculadora.component.css']
})
export class CalculadoraComponent implements OnInit{

  private numero1 : string = '0';
  private numero2: string = '0';
  private resultado:number = 0;
  private operacao: string = '';
  private guarda: string = '';

  constructor(private calculadoraService:CalculadoraService){}

  ngOnInit(): void {
    this.limpar();
  }


  limpar(): void {
    this.numero1 = '0';
    this.numero2 = '0';
    this.resultado = 0;
    this.operacao = '';
    this.guarda = '';
  }

  adicionarNumero(numero:string): void {
    this.guarda += numero;
    if(this.operacao === ''){
      this.numero1 = this.concatenarNumero(this.numero1, numero);
    }else{
      this.numero2 = this.concatenarNumero(this.numero2, numero);
    }
  }

  concatenarNumero(numAtual: string, numConcat: string): string {
    if(numAtual === '0'){
      numAtual = '';
    }
    if(numConcat === '.' && numAtual === ''){
      return '0.';
    }
    if(numConcat === '.' && numAtual.indexOf('.') > -1){
      return numAtual;
    }

    return numAtual + numConcat;
  }

  definirOperacao(operacao: string): void {
    this.guarda += operacao;
    if(this.operacao === ''){
      this.operacao = operacao;
      return;
    }

    if(this.numero2 !== null){
      this.resultado = this.calculadoraService.calcular(
        parseFloat(this.numero1),parseFloat(this.numero2),this.operacao);
        this.operacao = operacao;
        this.numero1 = this.resultado.toString();
        this.resultado = 0;
        this.numero2 = '0';
    }
  }

  calcular(): void {
    if(this.numero2 === '0'){
      return;
    }
    this.guarda += ' = ' + this.calculadoraService.calcular(
      parseFloat(this.numero1),parseFloat(this.numero2),this.operacao).toString();
  }

  display(agora:string): string{
    this.guarda += agora;
    if(this.resultado !== 0){
      this.guarda = this.resultado.toString();
    }
    if(this.numero2 !== '0'){
      this.guarda = this.numero2;
    }
    this.guarda = this.numero1;
    return this.guarda;
  }

  get retorno(): string{
    return this.guarda;
  }
}
<div class="calculator card" style="margin: 40px;">

  <input [value]="retorno" type="text" class="calculator-screen z-depth-1" value="" disabled />

  <div class="calculator-keys">

    <button (click)="definirOperacao('+')" type="button" class="operator btn btn-info" value="+">+</button>
    <button (click)="definirOperacao('-')" type="button" class="operator btn btn-info" value="-">-</button>
    <button (click)="definirOperacao('*')" type="button" class="operator btn btn-info" value="*">&times;</button>
    <button (click)="definirOperacao('/')" type="button" class="operator btn btn-info" value="/">&divide;</button>

    <button (click)="adicionarNumero('7')" type="button" value="7" class="btn btn-light waves-effect">7</button>
    <button (click)="adicionarNumero('8')" type="button" value="8" class="btn btn-light waves-effect">8</button>
    <button (click)="adicionarNumero('9')" type="button" value="9" class="btn btn-light waves-effect">9</button>


    <button (click)="adicionarNumero('4')" type="button" value="4" class="btn btn-light waves-effect">4</button>
    <button (click)="adicionarNumero('5')" type="button" value="5" class="btn btn-light waves-effect">5</button>
    <button (click)="adicionarNumero('6')" type="button" value="6" class="btn btn-light waves-effect">6</button>


    <button (click)="adicionarNumero('1')" type="button" value="1" class="btn btn-light waves-effect">1</button>
    <button (click)="adicionarNumero('2')" type="button" value="2" class="btn btn-light waves-effect">2</button>
    <button (click)="adicionarNumero('3')" type="button" value="3" class="btn btn-light waves-effect">3</button>


    <button (click)="adicionarNumero('0')" type="button" value="0" class="btn btn-light waves-effect">0</button>
    <button (click)="adicionarNumero('.')" type="button" class="decimal function btn btn-secondary" value=".">.</button>
    <button (click)="limpar()"type="button" class="all-clear function btn btn-danger btn-sm" value="all-clear">AC</button>

    <button (click)="calcular()" type="button" class="equal-sign operator btn btn-default" value="=">=</button>

  </div>
</div>
